import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [document, setDocument] = useState(null);

  const search = async () => {
    try {
      const response = await axios.get(`http://localhost:8000/search?query=${query}`);
      setResults(response.data);
      setDocument(null); // 검색 시 기존 미리 보기 숨기기
    } catch (error) {
      console.error('Error searching documents', error);
    }
  };

  const viewDocument = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8000/document/${id}`);
      setDocument(response.data);
    } catch (error) {
      console.error('Error fetching document', error);
    }
  };

  const uploadFile = async (event) => {
    event.preventDefault();
    const formData = new FormData();
    formData.append('file', event.target.file.files[0]);
    try {
      await axios.post('http://localhost:8000/uploadfile/', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });
      alert('File uploaded successfully');
    } catch (error) {
      console.error('Error uploading file', error);
    }
  };

  const deleteDocument = async (id) => {
    try {
      await axios.delete(`http://localhost:8000/document/${id}`);
      setResults(results.filter(result => result.id !== id));
      if (document && document.id === id) {
        setDocument(null);
      }
    } catch (error) {
      console.error('Error deleting document', error);
    }
  };

  return (
    <div className="App">
      <div className="search-container">
        <h1>Search Documents</h1>
        <div>
          <input 
            type="text" 
            value={query} 
            onChange={(e) => setQuery(e.target.value)} 
            placeholder="Enter search query"
          />
          <button onClick={search}>Search</button>
        </div>
      </div>
      <div className="results-container">
        {results.length === 0 && <p>No search results found.</p>}
        <div>
          {results.map(result => (
            <div key={result.id} className="result-item">
              <span>{result.title}: {result.content.substring(0, 100)}...</span>
              <button className="delete-button" onClick={() => deleteDocument(result.id)}>Delete</button>
            </div>
          ))}
        </div>
      </div>
      {document && (
        <div className="document-viewer">
          <h2>{document.title}</h2>
          <p>{document.content}</p>
          <button onClick={() => setDocument(null)}>Close</button>
        </div>
      )}
      <h2>Upload File</h2>
      <form onSubmit={uploadFile}>
        <input type="file" name="file" />
        <button type="submit">Upload</button>
      </form>
    </div>
  );
}

export default App;
