import React, { useState } from 'react';
import './App.css';
import axios from 'axios';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [noResults, setNoResults] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [file, setFile] = useState(null);
  const [selectedDocument, setSelectedDocument] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError('');
    setNoResults(false);
    setResults([]);
    setSelectedDocument(null); // 검색 시 기존 미리 보기 숨기기
    try {
      const response = await axios.get(`http://localhost:8000/search?query=${query}`);
      setResults(response.data);
      setNoResults(response.data.length === 0);
    } catch (err) {
      setError('An error occurred during the search. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!file) {
      setError('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await axios.post('http://localhost:8000/uploadfile/', formData);
      if (response.data.error) {
        setError(response.data.error);
      } else {
        setError('');
        alert('File uploaded successfully');
      }
    } catch (err) {
      setError('An error occurred during the file upload. Please try again.');
    }
  };

  const fetchDocument = async (id) => {
    try {
      const response = await axios.get(`http://localhost:8000/document/${id}`);
      setSelectedDocument(response.data);
    } catch (err) {
      setError('An error occurred while fetching the document. Please try again.');
    }
  };

  const deleteDocument = async (id) => {
    try {
      const response = await axios.delete(`http://localhost:8000/document/${id}`);
      alert('Document deleted successfully');
      handleSearch(); // Refresh the search results
    } catch (err) {
      setError('An error occurred while deleting the document. Please try again.');
    }
  };

  return (
    <div className="container">
      <h1>Search Documents</h1>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? 'Searching...' : 'Search'}
      </button>
      {error && <p className="error">{error}</p>}
      {noResults && <p className="no-results">No search results found.</p>}
      <div className="results">
        <ul>
          {results.map((result) => (
            <li key={result.id}>
              <div onClick={() => fetchDocument(result.id)}>
                {result.title}: {result.content}...
              </div>
              <button onClick={() => deleteDocument(result.id)}>Delete</button>
            </li>
          ))}
        </ul>
      </div>
      {selectedDocument && (
        <div className="document-details">
          <h2>{selectedDocument.title}</h2>
          <p>{selectedDocument.content}</p>
          <button onClick={() => setSelectedDocument(null)}>Close</button>
        </div>
      )}
      <h2>Upload File</h2>
      <label htmlFor="file-upload" className="custom-file-upload">
        {file ? file.name : 'Choose File'}
      </label>
      <input id="file-upload" type="file" onChange={handleFileChange} />
      <button onClick={handleFileUpload}>Upload</button>
    </div>
  );
}

export default App;
