import React, { useState } from 'react';

function App() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [noResults, setNoResults] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [file, setFile] = useState(null);

  const handleSearch = async () => {
    setLoading(true);
    setError('');
    setNoResults(false);
    setResults([]);
    try {
      const response = await fetch(`http://localhost:8000/search?query=${query}`);
      if (!response.ok) {
        throw new Error('Failed to fetch results');
      }
      const data = await response.json();
      setResults(data);
      setNoResults(data.length === 0);
    } catch (err) {
      setError('An error occurred during the search. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileUpload = async () => {
    if (!file) {
      setError('Please select a file to upload.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);

    try {
      const response = await fetch('http://localhost:8000/uploadfile/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Failed to upload file');
      }

      const data = await response.json();
      if (data.error) {
        setError(data.error);
      } else {
        setError('');
        alert('File uploaded successfully');
      }
    } catch (err) {
      setError('An error occurred during the file upload. Please try again.');
    }
  };

  return (
    <div>
      <h1>Search Documents</h1>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
      />
      <button onClick={handleSearch} disabled={loading}>
        {loading ? 'Searching...' : 'Search'}
      </button>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {noResults && <p>No search results found.</p>}
      <ul>
        {results.map((result) => (
          <li key={result.id}>{result.title}: {result.content}</li>
        ))}
      </ul>
      <h2>Upload File</h2>
      <input type="file" onChange={handleFileChange} />
      <button onClick={handleFileUpload}>Upload</button>
    </div>
  );
}

export default App;
